import React from "react";
import image from '../images'
import api from "../utils/Api";
import Card from "./Card";

export default function Main(props) {
    const currentUser = React.useContext(CurrentUserContext);

    return (
        <main className="main">
        <section className="profile">
          <div className="profile__info">
            <div className="profile__avatar"> 
            <img src={info.avatar} alt="Фото профиля" className ="profile__avatar-icon"/>
            <button className="profile__avatar-button"></button>
            </div> 
            <h1  className="profile__name">{info.name}</h1>
            <p className="profile__description">{info.about}</p>
            <button className="profile__edit-button" type="button"></button>
          </div>
          <button className="profile__add-button" type="button"></button>
        </section>
        <section className="card">
          {cards.map((card) => (
            <Card key={card._id} {...card} onCardClick={props.onCardClick} />
          ))}         
        </section>
        </main>
    );
  }
